  }

  private checkGovernanceCompliance(content: string): boolean {
    const requiredTags = ['@GL-governed', '@GL-layer:', '@GL-semantic:', '@GL-charter-version:'];
    return requiredTags.every(tag => content.includes(tag));
  }

