    if (!schema) {
      throw new Error(`Schema not found: ${schemaId}`);
    }

    const content = await fs.readFile(file, 'utf8');
    let data;

    try {
      if (schema.type === 'yaml') {
        data = yaml.load(content);
      } else if (schema.type === 'json') {
        data = JSON.parse(content);
      } else {
