import { ContentParser, ParsedContent } from './content-parsers';
import { SemanticClassifier, SemanticClassification } from './semantic-classifiers';
import { GLMapper, GLMapping } from './gl-mapping';
import { SchemaInferencer, SchemaInference } from './schema-infer';
import { IntentResolver, IntentResolution } from './intent-resolver';
import fs from 'fs/promises';
import path from 'path';

export interface SemanticAnalysis {
  filePath: string;
  parsedContent: ParsedContent;
  classification: SemanticClassification;
  mapping: GLMapping;
  schemaInference: SchemaInference;
  intentResolution: IntentResolution;
  analysisTimestamp: string;
  overallCompliance: boolean;
  issues: string[];
  recommendations: string[];
}

export class SemanticGraphOrchestrator {
  private contentParser: ContentParser;
  private semanticClassifier: SemanticClassifier;
  private glMapper: GLMapper;
  private schemaInferencer: SchemaInferencer;
  private intentResolver: IntentResolver;

  constructor() {
    this.contentParser = new ContentParser();
    this.semanticClassifier = new SemanticClassifier();
    this.glMapper = new GLMapper();
    this.schemaInferencer = new SchemaInferencer();
    this.intentResolver = new IntentResolver();
  }

  async analyzeFile(filePath: string): Promise<SemanticAnalysis> {
    const fileContent = await fs.readFile(filePath, 'utf-8');

    // Step 1: Parse content
    const parsedContent = await this.contentParser.parse(filePath);

    // Step 2: Classify semantics
    const classification = this.semanticClassifier.classify(filePath, parsedContent);

    // Step 3: Map to GL standards
    const mapping = this.glMapper.map(filePath, classification, fileContent);

    // Step 4: Infer schema
    const schemaInference = this.schemaInferencer.infer(filePath, parsedContent, classification);

    // Step 5: Resolve intent
    const intentResolution = this.intentResolver.resolve(filePath, parsedContent, classification, mapping);

    // Step 6: Compile overall analysis
    const overallCompliance = this.checkOverallCompliance(classification, mapping, schemaInference);
    const issues = this.compileIssues(classification, mapping, schemaInference);
    const recommendations = this.compileRecommendations(mapping, intentResolution);

    return {
      filePath,
      parsedContent,
      classification,
      mapping,
      schemaInference,
      intentResolution,
      analysisTimestamp: new Date().toISOString(),
      overallCompliance,
      issues,
      recommendations
    };
  }

  async analyzeDirectory(directoryPath: string, recursive: boolean = true): Promise<SemanticAnalysis[]> {
    const analyses: SemanticAnalysis[] = [];
    const files = await this.scanDirectory(directoryPath, recursive);

    for (const file of files) {
      try {
        const analysis = await this.analyzeFile(file);
        analyses.push(analysis);
      } catch (error) {
        console.error(`Error analyzing file ${file}:`, error);
      }
    }

    return analyses;
  }

  private async scanDirectory(directoryPath: string, recursive: boolean): Promise<string[]> {
    const files: string[] = [];
    const entries = await fs.readdir(directoryPath, { withFileTypes: true });

    for (const entry of entries) {
      const fullPath = path.join(directoryPath, entry.name);

      if (entry.isDirectory() && recursive) {
        // Skip node_modules and other common exclusions
        if (!entry.name.startsWith('.') && entry.name !== 'node_modules' && entry.name !== 'dist') {
          const subFiles = await this.scanDirectory(fullPath, recursive);
          files.push(...subFiles);
        }
      } else if (entry.isFile()) {
        // Include relevant file types
        const ext = path.extname(entry.name).toLowerCase();
        if (['.ts', '.js', '.py', '.yaml', '.yml', '.json', '.md'].includes(ext)) {
          files.push(fullPath);
        }
      }
    }

    return files;
  }

  private checkOverallCompliance(
    classification: SemanticClassification,
    mapping: GLMapping,
    schemaInference: SchemaInference
  ): boolean {
    return mapping.governanceCompliant &&
           classification.governanceCompliance.hasGovernedTag &&
           classification.governanceCompliance.hasSemanticAnchor &&
           classification.governanceCompliance.hasGLLayer &&
           classification.governanceCompliance.hasCharterVersion &&
           mapping.issues.length === 0;
  }

  private compileIssues(
    classification: SemanticClassification,
    mapping: GLMapping,
    schemaInference: SchemaInference
  ): string[] {
    const issues: string[] = [];

    issues.push(...mapping.issues);

    if (schemaInference.missingMetadata.length > 0) {
      issues.push(`Missing metadata: ${schemaInference.missingMetadata.join(', ')}`);
    }

    return issues;
  }

  private compileRecommendations(mapping: GLMapping, intentResolution: IntentResolution): string[] {
    const recommendations: string[] = [];

    recommendations.push(...intentResolution.recommendations);

    if (mapping.missingTags.length > 0) {
      recommendations.push(`Add missing tags: ${mapping.missingTags.join(', ')}`);
    }

    return recommendations;
  }

  async generateAutoRepairPatch(analysis: SemanticAnalysis): Promise<{ patch: string; filePath: string }> {
    const { filePath, mapping } = analysis;

    if (!mapping.governanceCompliant) {
      const fileContent = await fs.readFile(filePath, 'utf-8');
      const patchedContent = this.glMapper.applyGovernanceTags(fileContent, mapping);

      return {
        filePath,
        patch: patchedContent
      };
    }

    return {
      filePath,
      patch: ''
    };
  }

  async applyAutoRepairPatch(analysis: SemanticAnalysis): Promise<boolean> {
    const { patch, filePath } = await this.generateAutoRepairPatch(analysis);

    if (patch) {
      await fs.writeFile(filePath, patch, 'utf-8');
      return true;
    }

    return false;
  }
}

// @GL-governed
// @GL-layer: governance
// @GL-semantic: intent-resolver-index
// @GL-charter-version: 2.0.0

export { IntentResolver, IntentResolution } from './resolver';

// @GL-governed
// @GL-layer: governance
// @GL-semantic: intent-resolver
// @GL-charter-version: 2.0.0

import { ParsedContent } from '../content-parsers';
import { SemanticClassification } from '../semantic-classifiers';
import { GLMapping } from '../gl-mapping';

export interface IntentResolution {
  filePath: string;
  primaryIntent: string;
  secondaryIntents: string[];
  actions: string[];
  requiredCapabilities: string[];
  dependencies: string[];
  recommendations: string[];
  autoRepairCandidate: boolean;
  priority: 'low' | 'medium' | 'high' | 'critical';
}

export class IntentResolver {
  resolve(filePath: string, parsedContent: ParsedContent, classification: SemanticClassification, mapping: GLMapping): IntentResolution {
    const primaryIntent = this.determinePrimaryIntent(filePath, classification);
    const secondaryIntents = this.determineSecondaryIntents(filePath, classification);
    const actions = this.determineActions(filePath, classification, parsedContent);
    const requiredCapabilities = this.determineRequiredCapabilities(classification);
    const dependencies = this.determineDependencies(parsedContent, classification);
    const recommendations = this.generateRecommendations(filePath, classification, mapping);
    const autoRepairCandidate = this.isAutoRepairCandidate(classification, mapping);
    const priority = this.determinePriority(filePath, classification, mapping);

    return {
      filePath,
      primaryIntent,
      secondaryIntents,
      actions,
      requiredCapabilities,
      dependencies,
      recommendations,
      autoRepairCandidate,
      priority
    };
  }

  private determinePrimaryIntent(filePath: string, classification: SemanticClassification): string {
    const pathLower = filePath.toLowerCase();

    if (pathLower.includes('orchestrat')) return 'coordinate-multi-agent-execution';
    if (pathLower.includes('policy') || pathLower.includes('validator')) return 'enforce-governance-rules';
    if (pathLower.includes('repair') || pathLower.includes('fix')) return 'automatically-repair-violations';
    if (pathLower.includes('audit') || pathLower.includes('scan')) return 'audit-governance-compliance';
    if (pathLower.includes('deploy')) return 'deploy-changes-to-environment';
    if (pathLower.includes('api') || pathLower.includes('route')) return 'expose-api-endpoints';
    if (pathLower.includes('connector')) return 'integrate-with-external-systems';
    if (pathLower.includes('event')) return 'manage-governance-events';
    if (pathLower.includes('artifact')) return 'store-and-retrieve-artifacts';
    if (pathLower.includes('graph') || pathLower.includes('semantic')) return 'analyze-system-semantics';
    if (pathLower.includes('federation')) return 'coordinate-cross-repo-governance';

    // Determine from classification
    if (classification.role === 'agent') return 'execute-automated-tasks';
    if (classification.role === 'pipeline') return 'execute-orchestrated-workflow';
    if (classification.role === 'engine') return 'provide-core-runtime-capabilities';

    return 'provide-system-functionality';
  }

  private determineSecondaryIntents(filePath: string, classification: SemanticClassification): string[] {
    const intents: string[] = [];
    const pathLower = filePath.toLowerCase();

    // Common secondary intents
    intents.push('maintain-governance-compliance');
    intents.push('log-governance-events');

    // Path-specific secondary intents
    if (pathLower.includes('security')) intents.push('enforce-security-policies');
    if (pathLower.includes('quality')) intents.push('ensure-code-quality');
    if (pathLower.includes('monitor')) intents.push('monitor-system-health');
    if (pathLower.includes('sync')) intents.push('synchronize-data');
    if (pathLower.includes('federation')) intents.push('coordinate-across-organizations');

    // Classification-based secondary intents
    if (classification.criticality === 'critical' || classification.criticality === 'high') {
      intents.push('support-mission-critical-operations');
    }

    if (classification.governanceCompliance.hasGovernedTag) {
      intents.push('adhere-to-gl-governance-standards');
    }

    return [...new Set(intents)];
  }

  private determineActions(filePath: string, classification: SemanticClassification, parsedContent: ParsedContent): string[] {
    const actions: string[] = [];

    // Actions based on role
    if (classification.role === 'orchestrator') {
      actions.push('coordinate-agents');
      actions.push('manage-task-lifecycle');
      actions.push('handle-dependencies');
      actions.push('monitor-execution');
    }

    if (classification.role === 'agent') {
      actions.push('execute-tasks');
      actions.push('report-results');
      actions.push('handle-retries');
    }

    if (classification.role === 'validator') {
      actions.push('validate-schema');
      actions.push('check-compliance');
      actions.push('report-violations');
    }

    if (classification.role === 'connector') {
      actions.push('scan-repository');
      actions.push('generate-diffs');
      actions.push('create-patches');
      actions.push('apply-changes');
    }

    // Actions based on purpose
    if (classification.purpose.includes('audit')) {
      actions.push('scan-files');
      actions.push('validate-policies');
      actions.push('generate-reports');
    }

    if (classification.purpose.includes('repair')) {
      actions.push('identify-violations');
      actions.push('generate-fixes');
      actions.push('apply-patches');
    }

    if (classification.purpose.includes('api')) {
      actions.push('handle-requests');
      actions.push('validate-input');
      actions.push('execute-operations');
      actions.push('return-responses');
    }

    // Actions based on content
    if (parsedContent.functions) {
      actions.push(...parsedContent.functions.map(fn => `execute-${fn}`));
    }

    return [...new Set(actions)];
  }

  private determineRequiredCapabilities(classification: SemanticClassification): string[] {
    const capabilities: string[] = [];

    // Core capabilities
    capabilities.push('governance-awareness');
    capabilities.push('event-logging');
    capabilities.push('artifact-storage');

    // Role-specific capabilities
    if (classification.role === 'orchestrator') {
      capabilities.push('multi-agent-coordination');
      capabilities.push('task-scheduling');
      capabilities.push('dependency-resolution');
    }

    if (classification.role === 'validator') {
      capabilities.push('schema-validation');
      capabilities.push('policy-enforcement');
      capabilities.push('violation-detection');
    }

    if (classification.role === 'connector') {
      capabilities.push('git-operations');
      capabilities.push('file-system-access');
      capabilities.push('patch-generation');
    }

    // Purpose-specific capabilities
    if (classification.purpose.includes('semantic')) {
      capabilities.push('content-parsing');
      capabilities.push('semantic-analysis');
      capabilities.push('gl-mapping');
    }

    if (classification.purpose.includes('federation')) {
      capabilities.push('cross-repo-orchestration');
      capabilities.push('trust-validation');
      capabilities.push('event-aggregation');
    }

    return [...new Set(capabilities)];
  }

  private determineDependencies(parsedContent: ParsedContent, classification: SemanticClassification): string[] {
    const deps: string[] = [];

    // Extract from imports
    if (parsedContent.imports) {
      deps.push(...parsedContent.imports);
    }

    // Common governance dependencies
    deps.push('gl-policy-engine');
    deps.push('gl-event-stream');
    deps.push('gl-artifact-store');

    // Role-specific dependencies
    if (classification.role === 'orchestrator') {
      deps.push('gl-agent-registry');
      deps.push('task-queue');
    }

    if (classification.role === 'validator') {
      deps.push('schema-registry');
      deps.push('policy-store');
    }

    if (classification.role === 'connector') {
      deps.push('git-client');
      deps.push('file-scanner');
    }

    return [...new Set(deps)];
  }

  private generateRecommendations(filePath: string, classification: SemanticClassification, mapping: GLMapping): string[] {
    const recommendations: string[] = [];

    // Governance compliance recommendations
    if (!classification.governanceCompliance.hasGovernedTag) {
      recommendations.push('Add @GL-governed tag');
    }
    if (!classification.governanceCompliance.hasSemanticAnchor) {
      recommendations.push(`Add @GL-semantic: ${mapping.glSemanticAnchor}`);
    }
    if (!classification.governanceCompliance.hasGLLayer) {
      recommendations.push(`Add @GL-layer: ${mapping.glLayer}`);
    }
    if (!classification.governanceCompliance.hasCharterVersion) {
      recommendations.push('Add @GL-charter-version: 2.0.0');
    }

    // Path recommendations
    if (mapping.recommendedPath !== filePath) {
      recommendations.push(`Consider moving to recommended path: ${mapping.recommendedPath}`);
    }

    // Schema recommendations
    if (!classification.governanceCompliance.schemaCompliant) {
      recommendations.push('Ensure schema compliance with GL standards');
    }

    // Criticality-based recommendations
    if (classification.criticality === 'critical') {
      recommendations.push('Add comprehensive error handling');
      recommendations.push('Add health check endpoints');
      recommendations.push('Implement circuit breaker pattern');
    }

    return recommendations;
  }

  private isAutoRepairCandidate(classification: SemanticClassification, mapping: GLMapping): boolean {
    // Files are auto-repair candidates if:
    // 1. They have governance compliance issues
    // 2. They have missing tags
    // 3. They have mapping issues

    const hasComplianceIssues = !classification.governanceCompliance.hasGovernedTag ||
                                  !classification.governanceCompliance.hasSemanticAnchor ||
                                  !classification.governanceCompliance.hasGLLayer ||
                                  !classification.governanceCompliance.hasCharterVersion;

    const hasMissingTags = mapping.missingTags.length > 0;
    const hasIssues = mapping.issues.length > 0;

    return hasComplianceIssues || hasMissingTags || hasIssues;
  }

  private determinePriority(filePath: string, classification: SemanticClassification, mapping: GLMapping): IntentResolution['priority'] {
    // Critical: Governance core, orchestration engine, policy engine
    if (classification.criticality === 'critical') return 'critical';

    // High: Agents, APIs, connectors, federation components
    if (classification.criticality === 'high') return 'high';

    // Medium: Validators, scanners, monitors
    if (classification.criticality === 'medium') return 'medium';

    // High priority if it's an auto-repair candidate
    if (this.isAutoRepairCandidate(classification, mapping)) return 'high';

    // Low: Documentation, configuration
    return 'low';
  }
}

// @GL-governed
// @GL-layer: governance
// @GL-semantic: schema-inferencer-index
// @GL-charter-version: 2.0.0

export { SchemaInferencer, SchemaInference } from './inferencer';

// @GL-governed
// @GL-layer: governance
// @GL-semantic: schema-inferencer
// @GL-charter-version: 2.0.0

import { ParsedContent } from '../content-parsers';
import { SemanticClassification } from '../semantic-classifiers';

export interface SchemaInference {
  filePath: string;
  inferredSchema?: any;
  requiredFields: string[];
  optionalFields: string[];
  missingMetadata: string[];
  recommendedSchema?: string;
  schemaType: 'json-schema' | 'yaml-schema' | 'typescript-interface' | 'python-class' | 'unknown';
  confidence: number;
}

export class SchemaInferencer {
  infer(filePath: string, parsedContent: ParsedContent, classification: SemanticClassification): SchemaInference {
    const schemaType = this.determineSchemaType(parsedContent);
    const requiredFields = this.extractRequiredFields(parsedContent, classification);
    const optionalFields = this.extractOptionalFields(parsedContent, classification);
    const missingMetadata = this.identifyMissingMetadata(filePath, classification);
    const inferredSchema = this.generateInferredSchema(parsedContent, classification);
    const recommendedSchema = this.generateRecommendedSchema(filePath, classification);
    const confidence = this.calculateConfidence(parsedContent, classification);

    return {
      filePath,
      inferredSchema,
      requiredFields,
      optionalFields,
      missingMetadata,
      recommendedSchema,
      schemaType,
      confidence
    };
  }

  private determineSchemaType(content: ParsedContent): SchemaInference['schemaType'] {
    if (content.type === 'configuration' && content.format === 'yaml') return 'yaml-schema';
    if (content.type === 'data' && content.format === 'json') return 'json-schema';
    if (content.type === 'code') {
      if (content.language === 'typescript') return 'typescript-interface';
      if (content.language === 'python') return 'python-class';
    }
    return 'unknown';
  }

  private extractRequiredFields(content: ParsedContent, classification: SemanticClassification): string[] {
    const required: string[] = [];

    // Extract from code
    if (content.functions) {
      content.functions.forEach(fn => {
        // Look for required parameters
        if (fn.includes('required')) required.push(fn);
      });
    }

    // Extract from configuration
    if (content.type === 'configuration') {
      const contentStr = content.content.toString();
      const requiredMatch = contentStr.match(/required:\s*\[([^\]]+)\]/);
      if (requiredMatch) {
        const fields = requiredMatch[1].split(',').map((f: any) => f.trim().replace(/['"]/g, ''));
        required.push(...fields);
      }
    }

    // Add governance-specific required fields
    if (classification.role === 'agent' || classification.role === 'pipeline') {
      required.push('name', 'type', 'priority', 'enabled', 'config');
    }

    if (classification.role === 'configuration' || content.type === 'configuration') {
      required.push('apiVersion', 'kind', 'metadata', 'spec');
    }

    return [...new Set(required)];
  }

  private extractOptionalFields(content: ParsedContent, classification: SemanticClassification): string[] {
    const optional: string[] = [];

    // Extract from code
    if (content.functions) {
      content.functions.forEach(fn => {
        if (fn.includes('optional')) optional.push(fn);
      });
    }

    // Common optional fields for governance
    optional.push('annotations', 'labels', 'description', 'version', 'created_at');

    return [...new Set(optional)];
  }

  private identifyMissingMetadata(filePath: string, classification: SemanticClassification): string[] {
    const missing: string[] = [];

    // Check for required metadata fields
    if (classification.role === 'agent' || classification.role === 'pipeline') {
      missing.push('metadata.name');
      missing.push('metadata.version');
      missing.push('metadata.owner');
    }

    if (classification.purpose.includes('api')) {
      missing.push('spec.apiVersion');
      missing.push('spec.endpoints');
    }

    return [...new Set(missing)];
  }

  private generateInferredSchema(content: ParsedContent, classification: SemanticClassification): any {
    const schema: any = {
      type: 'object',
      properties: {},
      required: []
    };

    // Add fields based on classification
    if (classification.role === 'agent') {
      schema.properties = {
        id: { type: 'string' },
        name: { type: 'string' },
        type: { type: 'string' },
        priority: { type: 'number' },
        enabled: { type: 'boolean' },
        config: { type: 'object' },
        dependencies: { type: 'array', items: { type: 'string' } },
        outputs: { type: 'array', items: { type: 'string' } }
      };
      schema.required = ['id', 'name', 'type', 'priority', 'enabled', 'config'];
    }

    if (content.apiEndpoints && content.apiEndpoints.length > 0) {
      schema.properties.apiEndpoints = {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            path: { type: 'string' },
            method: { type: 'string', enum: ['GET', 'POST', 'PUT', 'DELETE', 'PATCH'] }
          }
        }
      };
    }

    if (content.functions && content.functions.length > 0) {
      schema.properties.functions = {
        type: 'array',
        items: { type: 'string' }
      };
      schema.properties.functions.items.enum = content.functions;
    }

    return schema;
  }

  private generateRecommendedSchema(filePath: string, classification: SemanticClassification): string {
    const fileName = filePath.split('/').pop() || '';
    const baseName = fileName.replace(/\.(ts|js|py|yaml|yml|json)$/, '');

    let schema = '';

    if (classification.role === 'agent' || classification.purpose === 'agent') {
      schema = `apiVersion: orchestration.machinenativeops.io/v1
kind: Agent
metadata:
  name: ${baseName}
  version: "1.0.0"
  created_at: "${new Date().toISOString()}"
  layer: "${classification.glLayer}"
  owner: "engineering-team"
  annotations:
    governance.machinenativeops.io/classification: "internal"
    governance.machinenativeops.io/approval: "approved"
    governance.machinenativeops.io/charter-version: "2.0.0"

spec:
  enabled: true
  config:
    validation_scope:
      - "engine"
      - "governance"
    strict_mode: true
    enforce_semantic_anchors: true
  dependencies: []
  outputs:
    - "${baseName}-report.json"
    - "${baseName}-summary.md"`;
    } else if (classification.role === 'pipeline' || classification.purpose.includes('pipeline')) {
      schema = `apiVersion: pipeline.machinenativeops.io/v1
kind: Pipeline
metadata:
  name: ${baseName}
  version: "1.0.0"
  created_at: "${new Date().toISOString()}"
  layer: "${classification.glLayer}"
  owner: "engineering-team"

spec:
  enabled: true
  stages:
    - name: validate
      enabled: true
    - name: fix
      enabled: true
    - name: deploy
      enabled: false
  triggers:
    - event: "gl-audit-complete"
    - event: "gl-violation-detected"`;
    }

    return schema;
  }

  private calculateConfidence(content: ParsedContent, classification: SemanticClassification): number {
    let confidence = 0.3; // Base confidence

    // Increase confidence based on content analysis
    if (content.type !== 'text') confidence += 0.1;
    if (classification.role !== 'resource') confidence += 0.1;
    if (classification.purpose !== 'general-purpose') confidence += 0.1;
    if (content.functions && content.functions.length > 0) confidence += 0.1;
    if (content.classes && content.classes.length > 0) confidence += 0.1;
    if (content.apiEndpoints && content.apiEndpoints.length > 0) confidence += 0.1;
    if (content.schemas && content.schemas.length > 0) confidence += 0.1;

    // Increase confidence if governance tags are present
    if (classification.governanceCompliance.hasSemanticAnchor) confidence += 0.1;
    if (classification.governanceCompliance.hasGLLayer) confidence += 0.1;

    return Math.min(1.0, Math.max(0.0, confidence));
  }

  validateSchema(filePath: string, schema: any): boolean {
    // Basic schema validation
    if (!schema || typeof schema !== 'object') return false;
    if (!schema.properties) return false;
    if (!Array.isArray(schema.required)) return false;

    return true;
  }
}

// @GL-governed
// @GL-layer: governance
// @GL-semantic: semantic-classifier
// @GL-charter-version: 2.0.0

import { ParsedContent } from '../content-parsers';

export interface SemanticClassification {
  role: string;
  purpose: string;
  category: string;
  glLayer: string;
  semanticAnchor: string;
  criticality: 'low' | 'medium' | 'high' | 'critical';
  dependencies: string[];
  provides: string[];
  governanceCompliance: {
    hasSemanticAnchor: boolean;
    hasGLLayer: boolean;
    hasCharterVersion: boolean;
    hasGovernedTag: boolean;
    schemaCompliant: boolean;
  };
}

export class SemanticClassifier {
  classify(filePath: string, parsedContent: ParsedContent): SemanticClassification {
    const pathSegments = filePath.split('/');
    const fileName = pathSegments[pathSegments.length - 1];

    // Determine role based on path and content
    const role = this.determineRole(filePath, parsedContent);
    const purpose = this.determinePurpose(filePath, parsedContent);
    const category = this.determineCategory(filePath, parsedContent);
    const glLayer = this.determineGLLayer(filePath, parsedContent);
    const semanticAnchor = this.determineSemanticAnchor(filePath, parsedContent);
    const criticality = this.determineCriticality(filePath, parsedContent);
    const dependencies = this.extractDependencies(filePath, parsedContent);
    const provides = this.extractProvides(filePath, parsedContent);
    const governanceCompliance = this.checkGovernanceCompliance(filePath, parsedContent);

    return {
      role,
      purpose,
      category,
      glLayer,
      semanticAnchor,
      criticality,
      dependencies,
      provides,
      governanceCompliance
    };
  }

  private determineRole(filePath: string, content: ParsedContent): string {
    const pathLower = filePath.toLowerCase();

    if (pathLower.includes('orchestrat')) return 'orchestrator';
    if (pathLower.includes('pipeline')) return 'pipeline';
    if (pathLower.includes('agent')) return 'agent';
    if (pathLower.includes('connector')) return 'connector';
    if (pathLower.includes('scanner')) return 'scanner';
    if (pathLower.includes('validator')) return 'validator';
    if (pathLower.includes('policy')) return 'policy';
    if (pathLower.includes('engine')) return 'engine';
    if (pathLower.includes('runtime')) return 'runtime';
    if (pathLower.includes('api')) return 'api';
    if (pathLower.includes('route')) return 'route';
    if (pathLower.includes('model')) return 'data-model';
    if (pathLower.includes('schema')) return 'schema';
    if (pathLower.includes('config')) return 'configuration';
    if (pathLower.includes('hook')) return 'hook';
    if (pathLower.includes('docker') || pathLower.includes('k8s') || pathLower.includes('deployment')) return 'deployment';
    if (pathLower.includes('readme')) return 'documentation';
    if (content.type === 'code') {
      if (content.classes && content.classes.length > 0) return 'class';
      if (content.functions && content.functions.length > 0) return 'module';
    }

    return 'resource';
  }

  private determinePurpose(filePath: string, content: ParsedContent): string {
    const pathLower = filePath.toLowerCase();

    if (pathLower.includes('audit')) return 'governance-audit';
    if (pathLower.includes('repair') || pathLower.includes('fix')) return 'auto-repair';
    if (pathLower.includes('deploy')) return 'deployment';
    if (pathLower.includes('sync')) return 'synchronization';
    if (pathLower.includes('monitor')) return 'monitoring';
    if (pathLower.includes('security')) return 'security';
    if (pathLower.includes('quality')) return 'quality-assurance';
    if (pathLower.includes('scan')) return 'scanning';
    if (pathLower.includes('event')) return 'event-management';
    if (pathLower.includes('artifact')) return 'artifact-storage';
    if (pathLower.includes('index')) return 'indexing';
    if (pathLower.includes('graph')) return 'graph-management';
    if (pathLower.includes('semantic')) return 'semantic-analysis';
    if (pathLower.includes('federation')) return 'federation-governance';

    if (content.apiEndpoints && content.apiEndpoints.length > 0) return 'api-exposure';
    if (content.schemas && content.schemas.length > 0) return 'schema-definition';
    if (content.functions && content.functions.length > 0) return 'functionality';
    if (content.classes && content.classes.length > 0) return 'behavior-implementation';

    return 'general-purpose';
  }

  private determineCategory(filePath: string, content: ParsedContent): string {
    if (content.type === 'code') {
      if (filePath.includes('test') || filePath.includes('spec')) return 'testing';
      if (filePath.includes('src/')) return 'source';
      return 'code';
    }
    if (content.type === 'configuration') return 'configuration';
    if (content.type === 'data') return 'data';
    if (content.type === 'documentation') return 'documentation';
    return 'other';
  }

  private determineGLLayer(filePath: string, content: ParsedContent): string {
    const pathLower = filePath.toLowerCase();

    if (pathLower.includes('governance') || pathLower.includes('policy') || pathLower.includes('audit')) return 'GL90-99';
    if (pathLower.includes('engine') || pathLower.includes('runtime') || pathLower.includes('orchestrat')) return 'GL70-89';
    if (pathLower.includes('federation')) return 'GL50-69';
    if (pathLower.includes('agent')) return 'GL30-49';
    if (pathLower.includes('connector') || pathLower.includes('scanner')) return 'GL10-29';
    if (pathLower.includes('deployment') || pathLower.includes('ops')) return 'GL00-09';

    return 'GL00-99';
  }

  private determineSemanticAnchor(filePath: string, content: ParsedContent): string {
    const fileName = filePath.split('/').pop() || '';
    const baseName = fileName.replace(/\.(ts|js|py|yaml|yml|json|md)$/, '');

    const pathLower = filePath.toLowerCase();

    if (pathLower.includes('orchestrat')) return 'orchestrator-engine';
    if (pathLower.includes('policy')) return 'policy-engine';
    if (pathLower.includes('event')) return 'event-stream';
    if (pathLower.includes('artifact')) return 'artifact-store';
    if (pathLower.includes('scanner')) return 'resource-scanner';
    if (pathLower.includes('indexer')) return 'resource-indexer';
    if (pathLower.includes('graph')) return 'resource-graph';
    if (pathLower.includes('semantic')) return 'semantic-analysis';
    if (pathLower.includes('federation')) return 'federation-governance';

    if (content.type === 'code') {
      if (content.classes && content.classes.length > 0) {
        return content.classes[0].toLowerCase().replace(/[^a-z0-9]/g, '-');
      }
      if (content.functions && content.functions.length > 0) {
        return content.functions[0].toLowerCase().replace(/[^a-z0-9]/g, '-');
      }
    }

    return baseName.toLowerCase().replace(/[^a-z0-9]/g, '-');
  }

  private determineCriticality(filePath: string, content: ParsedContent): 'low' | 'medium' | 'high' | 'critical' {
    const pathLower = filePath.toLowerCase();

    if (pathLower.includes('engine') || pathLower.includes('orchestrat') || pathLower.includes('runtime')) {
      return 'critical';
    }
    if (pathLower.includes('governance') || pathLower.includes('policy') || pathLower.includes('security')) {
      return 'critical';
    }
    if (pathLower.includes('api') || pathLower.includes('route') || pathLower.includes('connector')) {
      return 'high';
    }
    if (pathLower.includes('federation') || pathLower.includes('federation')) {
      return 'high';
    }
    if (pathLower.includes('agent') || pathLower.includes('pipeline')) {
      return 'high';
    }
    if (pathLower.includes('scanner') || pathLower.includes('validator')) {
      return 'medium';
    }
    if (pathLower.includes('monitor') || pathLower.includes('event')) {
      return 'medium';
    }

    return 'low';
  }

  private extractDependencies(filePath: string, content: ParsedContent): string[] {
    const deps: string[] = [];

    if (content.imports) {
      content.imports.forEach(imp => {
        if (!imp.startsWith('.') && !imp.startsWith('/')) {
          deps.push(imp);
        }
      });
    }

    return [...new Set(deps)];
  }

  private extractProvides(filePath: string, content: ParsedContent): string[] {
    const provides: string[] = [];

    if (content.exports) {
      content.exports.forEach(exp => provides.push(exp));
    }
    if (content.apiEndpoints) {
      content.apiEndpoints.forEach(ep => {
        if (ep.path && ep.method) {
          provides.push(`${ep.method} ${ep.path}`);
        }
      });
    }

    return [...new Set(provides)];
  }

  private checkGovernanceCompliance(filePath: string, content: ParsedContent): SemanticClassification['governanceCompliance'] {
    const contentStr = content.content.toString();

    return {
      hasSemanticAnchor: /@GL-semantic:/.test(contentStr),
      hasGLLayer: /@GL-layer:/.test(contentStr),
      hasCharterVersion: /@GL-charter-version:/.test(contentStr),
      hasGovernedTag: /@GL-governed/.test(contentStr),
      schemaCompliant: this.checkSchemaCompliance(filePath, content)
    };
  }

  private checkSchemaCompliance(filePath: string, content: ParsedContent): boolean {
    // Basic schema compliance check
    if (content.type === 'configuration') {
      const contentStr = content.content.toString();
      return /apiVersion:|kind:|metadata:|spec:/.test(contentStr);
    }
    return true;
  }
}

