import simpleGit, { SimpleGit } from 'simple-git';
import path from 'path';
import fs from 'fs/promises';
import { createLogger } from '../utils/logger';
import { v4 as uuidv4 } from 'uuid';

const logger = createLogger('GitConnector');

export interface GitDiffResult {
  filePath: string;
  status: string;
  changes: string;
}

export interface GitPatch {
  id: string;
  filePath: string;
  originalContent: string;
  modifiedContent: string;
  patch: string;
}

export class GitConnector {
  private git: SimpleGit;
  private repoPath: string;

  constructor(repoPath?: string) {
    this.repoPath = repoPath || process.cwd();
    this.git = simpleGit(this.repoPath);
  }

  public async scanRepository(): Promise<string[]> {
    try {
      const status = await this.git.status();
      const files = [
        ...status.files.map((f: any) => f.path),
        ...status.staged,
        ...status.modified
      ];

      logger.info(`Scanned ${files.length} files in repository`);
      return Array.from(new Set(files));
    } catch (error: any) {
      logger.error(`Failed to scan repository: ${error.message}`);
      throw error;
    }
  }

  public async getDiff(filePath: string): Promise<GitDiffResult> {
    try {
      const diff = await this.git.diff([filePath]);

      const result: GitDiffResult = {
        filePath,
        status: 'modified',
        changes: diff
      };

      logger.info(`Generated diff for ${filePath}`);
      return result;
    } catch (error: any) {
      logger.error(`Failed to get diff for ${filePath}: ${error.message}`);
      throw error;
    }
  }

  public async createPatch(filePath: string, modifications: string): Promise<GitPatch> {
    try {
      const fullPath = path.join(this.repoPath, filePath);
      const originalContent = await fs.readFile(fullPath, 'utf-8');

      const patch: GitPatch = {
        id: uuidv4(),
        filePath,
        originalContent,
        modifiedContent: modifications,
        patch: this.generatePatchString(originalContent, modifications)
      };

      logger.info(`Created patch for ${filePath}`);
      return patch;
    } catch (error: any) {
      logger.error(`Failed to create patch for ${filePath}: ${error.message}`);
      throw error;
    }
  }

  private generatePatchString(original: string, modified: string): string {
    const originalLines = original.split('\n');
    const modifiedLines = modified.split('\n');

    let patch = `--- a/\n+++ b/\n`;

    // Simplified diff generation
    if (original !== modified) {
      patch += `@@ -1,${originalLines.length} +1,${modifiedLines.length} @@\n`;
      patch += originalLines.map(line => `- ${line}`).join('\n') + '\n';
      patch += modifiedLines.map(line => `+ ${line}`).join('\n');
    }

    return patch;
  }

  public async applyPatch(patch: GitPatch): Promise<void> {
    try {
      const fullPath = path.join(this.repoPath, patch.filePath);
      await fs.writeFile(fullPath, patch.modifiedContent, 'utf-8');

      logger.info(`Applied patch to ${patch.filePath}`);
    } catch (error: any) {
      logger.error(`Failed to apply patch: ${error.message}`);
      throw error;
    }
  }

  public async add(filePath: string): Promise<void> {
    try {
      await this.git.add(filePath);
      logger.info(`Staged file: ${filePath}`);
    } catch (error: any) {
      logger.error(`Failed to stage file ${filePath}: ${error.message}`);
      throw error;
    }
  }

  public async commit(message: string): Promise<string> {
    try {
      const result = await this.git.commit(message);
      logger.info(`Committed changes: ${result.commit}`);
      return result.commit;
    } catch (error: any) {
      logger.error(`Failed to commit: ${error.message}`);
      throw error;
    }
  }

  public async push(branch: string = 'main'): Promise<void> {
    try {
      await this.git.push('origin', branch);
      logger.info(`Pushed to origin/${branch}`);
    } catch (error: any) {
      logger.error(`Failed to push: ${error.message}`);
      throw error;
    }
  }

  public async createBranch(branchName: string): Promise<void> {
    try {
      await this.git.checkoutLocalBranch(branchName);
      logger.info(`Created branch: ${branchName}`);
    } catch (error: any) {
      logger.error(`Failed to create branch: ${error.message}`);
      throw error;
    }
  }

  public async getStatus(): Promise<any> {
    try {
      const status = await this.git.status();
      return status;
    } catch (error: any) {
      logger.error(`Failed to get status: ${error.message}`);
      throw error;
    }
  }
}

// @GL-governed
// @GL-layer: GL90-99
// @GL-semantic: event-stream-manager
// @GL-charter-version: 2.0.0

import fs from 'fs/promises';
import path from 'path';
import { createLogger } from '../utils/logger';

const logger = createLogger('EventStreamManager');

export interface GovernanceEvent {
  eventType: string;
  layer: string;
  semanticAnchor: string;
  timestamp: string;
  metadata: any;
}

export class EventStreamManager {
  private eventStreamPath: string;
  private events: GovernanceEvent[] = [];

  constructor() {
    this.eventStreamPath = path.join(process.cwd(), 'storage', 'gl-events-stream', 'events.jsonl');
  }

  public async logEvent(event: GovernanceEvent): Promise<void> {
    this.events.push(event);
    await this.persistEvent(event);
    logger.info(`Event logged: ${event.eventType}`);
  }

  private async persistEvent(event: GovernanceEvent): Promise<void> {
    try {
      const dir = path.dirname(this.eventStreamPath);
      await fs.mkdir(dir, { recursive: true });

      const eventLine = JSON.stringify(event) + '\n';
      await fs.appendFile(this.eventStreamPath, eventLine);
    } catch (error: any) {
      logger.error(`Failed to persist event: ${error.message}`);
      throw error;
    }
  }

  public async loadEvents(): Promise<GovernanceEvent[]> {
    try {
      const content = await fs.readFile(this.eventStreamPath, 'utf-8');
      const lines = content.trim().split('\n');
      this.events = lines.map(line => JSON.parse(line));
      return this.events;
    } catch (error: any) {
      if (error.code === 'ENOENT') {
        return [];
      }
      throw error;
    }
  }

  public async getEventsByType(eventType: string): Promise<GovernanceEvent[]> {
    const allEvents = await this.loadEvents();
    return allEvents.filter(event => event.eventType === eventType);
  }

  public async getEventsByLayer(layer: string): Promise<GovernanceEvent[]> {
    const allEvents = await this.loadEvents();
    return allEvents.filter(event => event.layer === layer);
  }

  public async getEventsByTimeRange(startTime: string, endTime: string): Promise<GovernanceEvent[]> {
    const allEvents = await this.loadEvents();
    return allEvents.filter(event => 
      event.timestamp >= startTime && event.timestamp <= endTime
    );
  }

  public getEventCount(): number {
    return this.events.length;
  }

  public clearEvents(): void {
    this.events = [];
  }
}

// @GL-governed
// @GL-layer: GL90-99
// @GL-semantic: platform-entry-point
// @GL-charter-version: 2.0.0

import express from 'express';
import { OrchestratorEngine } from './orchestration/orchestrator-engine';
import { PolicyEngine } from './policies/policy-engine';
import { EventStreamManager } from './events/event-stream-manager';
import { ArtifactStore } from './storage/artifact-store';
import { GitConnector } from './connectors/git-connector';
import { createLogger } from './utils/logger';
import { setupRoutes } from './api/routes';

const logger = createLogger('GL-Runtime-Platform');

export class GLRuntimePlatform {
  private app: express.Application;
  private orchestrator: OrchestratorEngine;
  private policyEngine: PolicyEngine;
  private eventStream: EventStreamManager;
  private artifactStore: ArtifactStore;
  private gitConnector: GitConnector;
  private srgRuntime: any;

  constructor() {
    this.app = express();
    this.orchestrator = new OrchestratorEngine();
    this.policyEngine = new PolicyEngine();
    this.eventStream = new EventStreamManager();
    this.artifactStore = new ArtifactStore();
    this.gitConnector = new GitConnector();

    // SRG will be initialized dynamically
    this.srgRuntime = null;

    this.initialize();
  }

  private async initialize(): Promise<void> {
    logger.info('Initializing GL Runtime Platform v7.0.0');

    // Log governance event
    await this.eventStream.logEvent({
      eventType: 'platform_init',
      layer: 'GL90-99',
      semanticAnchor: 'GL-ROOT-GOVERNANCE',
      timestamp: new Date().toISOString(),
      metadata: {
        version: '7.0.0',
        charterVersion: '2.0.0',
        semanticGraphEnabled: true
      }
    });

    // Initialize Semantic Graph Runtime dynamically
    try {
      const { SemanticGraphRuntime } = await import('./engine/semantic-graph-runtime');
      this.srgRuntime = new SemanticGraphRuntime({
        autoRefreshInterval: 300,
        storagePath: 'storage/gl-semantic-graph/',
        enabled: true
      });
      await this.srgRuntime.initialize(process.cwd());
      logger.info('Semantic Graph Runtime initialized');
    } catch (error) {
      logger.warn('Semantic Graph Runtime initialization failed:', error);
    }

    // Setup routes with SRG runtime
    setupRoutes(this.app, {
      orchestrator: this.orchestrator,
      policyEngine: this.policyEngine,
      eventStream: this.eventStream,
      artifactStore: this.artifactStore,
      gitConnector: this.gitConnector,
      srgRuntime: this.srgRuntime
    });

    logger.info('GL Runtime Platform initialized successfully');
  }

  public async start(port: number = 3000): Promise<void> {
    return new Promise((resolve) => {
      this.app.listen(port, () => {
        const srgStatus = this.srgRuntime?.getStatus() || {
          ready: false,
          totalFilesAnalyzed: 0,
          compliantFiles: 0
        };
        logger.info(`GL Runtime Platform listening on port ${port}`);
        logger.info(`Semantic Graph Runtime: ${srgStatus.ready ? 'Ready' : 'Not Ready'}`);
        logger.info(`Files analyzed: ${srgStatus.totalFilesAnalyzed}`);
        logger.info(`Compliant files: ${srgStatus.compliantFiles}`);
        resolve();
      });
    });
  }

  public getApp(): express.Application {
    return this.app;
  }

  public getSRGRuntime(): any {
    return this.srgRuntime;
  }
}

// Main entry point
async function main() {
  const platform = new GLRuntimePlatform();
  await platform.start(3000);
}

if (require.main === module) {
  main().catch((error) => {
    logger.error('Failed to start platform', error);
    process.exit(1);
  });
}

export default GLRuntimePlatform;

// @GL-governed
// @GL-layer: GL90-99
// @GL-semantic: multi-agent-orchestrator
// @GL-charter-version: 2.0.0

import { v4 as uuidv4 } from 'uuid';
import { EventStreamManager } from '../events/event-stream-manager';
import { PolicyEngine } from '../policies/policy-engine';
import { createLogger } from '../utils/logger';

const logger = createLogger('OrchestratorEngine');

export interface AgentConfig {
  id: string;
  name: string;
  type: string;
  priority: number;
  enabled: boolean;
  config: any;
  dependencies: string[];
  outputs: string[];
}

export interface ExecutionTask {
  id: string;
  agentId: string;
  filePath?: string;
  input: any;
  status: 'pending' | 'running' | 'completed' | 'failed';
  result?: any;
  error?: string;
  startTime?: string;
  endTime?: string;
}

export class OrchestratorEngine {
  private agents: Map<string, AgentConfig> = new Map();
  private taskQueue: ExecutionTask[] = [];
  private runningTasks: Map<string, ExecutionTask> = new Map();
  private completedTasks: Map<string, ExecutionTask> = new Map();
  private maxConcurrentAgents: number = 8;
  private eventStream: EventStreamManager;
  private policyEngine: PolicyEngine;

  constructor() {
    this.eventStream = new EventStreamManager();
    this.policyEngine = new PolicyEngine();
  }

  public registerAgent(agent: AgentConfig): void {
    this.agents.set(agent.id, agent);
    logger.info(`Agent registered: ${agent.id}`);
  }

  public async executePerFilePipeline(filePath: string): Promise<void> {
    const taskId = uuidv4();

    // Create task for each agent
    for (const [agentId, agent] of this.agents) {
      if (!agent.enabled) continue;

      const task: ExecutionTask = {
        id: uuidv4(),
        agentId,
        filePath,
        input: { filePath },
        status: 'pending'
      };

      this.taskQueue.push(task);
    }

    // Execute tasks in dependency order
    await this.executeTasks();

    // Log governance event
    await this.eventStream.logEvent({
      eventType: 'pipeline_execution',
      layer: 'GL90-99',
      semanticAnchor: 'GL-ROOT-GOVERNANCE',
      timestamp: new Date().toISOString(),
      metadata: {
        filePath,
        totalTasks: this.taskQueue.length,
        completedTasks: this.completedTasks.size
      }
    });
  }

  private async executeTasks(): Promise<void> {
    while (this.taskQueue.length > 0 || this.runningTasks.size > 0) {
      // Start new tasks if capacity available
      while (this.runningTasks.size < this.maxConcurrentAgents && this.taskQueue.length > 0) {
        const task = this.taskQueue.shift()!;
        await this.executeTask(task);
      }

      // Wait a bit before next iteration
      await new Promise(resolve => setTimeout(resolve, 100));
    }
  }

  private async executeTask(task: ExecutionTask): Promise<void> {
    const agent = this.agents.get(task.agentId);
    if (!agent) {
      task.status = 'failed';
      task.error = 'Agent not found';
      return;
    }

    task.status = 'running';
    task.startTime = new Date().toISOString();
    this.runningTasks.set(task.id, task);

    logger.info(`Executing task ${task.id} with agent ${agent.id}`);

    try {
      // Validate against policy engine
      const validationResult = await this.policyEngine.validate(task.input, agent.config);

      if (!validationResult.valid) {
        throw new Error(`Policy validation failed: ${validationResult.error}`);
      }

      // Execute agent logic (placeholder for actual agent implementation)
      const result = await this.executeAgentLogic(agent, task);

      task.status = 'completed';
      task.result = result;
      task.endTime = new Date().toISOString();

      logger.info(`Task ${task.id} completed successfully`);

    } catch (error: any) {
      task.status = 'failed';
      task.error = error.message;
      task.endTime = new Date().toISOString();

      logger.error(`Task ${task.id} failed: ${error.message}`);

      await this.eventStream.logEvent({
        eventType: 'task_failed',
        layer: 'GL90-99',
        semanticAnchor: 'GL-ROOT-GOVERNANCE',
        timestamp: new Date().toISOString(),
        metadata: {
          taskId: task.id,
          agentId: task.agentId,
          error: error.message
        }
      });
    } finally {
      this.runningTasks.delete(task.id);
      this.completedTasks.set(task.id, task);
    }
  }

  private async executeAgentLogic(agent: AgentConfig, task: ExecutionTask): Promise<any> {
    // Placeholder for actual agent implementation
    // Each agent would have its own execution logic here

    const result = {
      agentId: agent.id,
      agentName: agent.name,
      success: true,
      outputs: agent.outputs,
      metadata: {
        executionTime: Date.now(),
        timestamp: new Date().toISOString()
      }
    };

    return result;
  }

  public getTaskStatus(taskId: string): ExecutionTask | undefined {
    return this.completedTasks.get(taskId) || this.runningTasks.get(taskId);
  }

  public getAllCompletedTasks(): ExecutionTask[] {
    return Array.from(this.completedTasks.values());
  }

  public reset(): void {
    this.taskQueue = [];
    this.runningTasks.clear();
    this.completedTasks.clear();
  }
}

// @GL-governed
// @GL-layer: GL90-99
// @GL-semantic: policy-engine
// @GL-charter-version: 2.0.0

import Ajv from 'ajv';
import { EventStreamManager } from '../events/event-stream-manager';
import { createLogger } from '../utils/logger';

const logger = createLogger('PolicyEngine');

export interface ValidationResult {
  valid: boolean;
  error?: string;
  violations?: any[];
}

export interface PolicyConfig {
  schemaValidation: boolean;
  namingValidation: boolean;
  pathValidation: boolean;
  semanticValidation: boolean;
  governanceTagValidation: boolean;
}

export class PolicyEngine {
  private ajv: Ajv;
  private eventStream: EventStreamManager;
  private policies: PolicyConfig;

  constructor() {
    this.ajv = new Ajv({ allErrors: true });
    this.eventStream = new EventStreamManager();
    this.policies = {
      schemaValidation: true,
      namingValidation: true,
      pathValidation: true,
      semanticValidation: true,
      governanceTagValidation: true
    };
  }

  public async validate(input: any, config: any): Promise<ValidationResult> {
    const violations: any[] = [];

    // Schema validation
    if (this.policies.schemaValidation && config.schema) {
      const schemaResult = this.validateSchema(input, config.schema);
      if (!schemaResult.valid) {
        violations.push({
          type: 'schema',
          violations: schemaResult.violations
        });
      }
    }

    // Naming validation
    if (this.policies.namingValidation) {
      const namingResult = this.validateNaming(input);
      if (!namingResult.valid) {
        violations.push({
          type: 'naming',
          violations: namingResult.violations
        });
      }
    }

    // Path validation
    if (this.policies.pathValidation && input.filePath) {
      const pathResult = this.validatePath(input.filePath);
      if (!pathResult.valid) {
        violations.push({
          type: 'path',
          violations: pathResult.violations
        });
      }
    }

    // Semantic validation
    if (this.policies.semanticValidation) {
      const semanticResult = this.validateSemantic(input);
      if (!semanticResult.valid) {
        violations.push({
          type: 'semantic',
          violations: semanticResult.violations
        });
      }
    }

    // Governance tag validation
    if (this.policies.governanceTagValidation) {
      const governanceResult = this.validateGovernanceTags(input);
      if (!governanceResult.valid) {
        violations.push({
          type: 'governance',
          violations: governanceResult.violations
        });
      }
    }

    const isValid = violations.length === 0;

    // Log validation event
    await this.eventStream.logEvent({
      eventType: 'policy_validation',
      layer: 'GL90-99',
      semanticAnchor: 'GL-ROOT-GOVERNANCE',
      timestamp: new Date().toISOString(),
      metadata: {
        valid: isValid,
        violationsCount: violations.length,
        violations: violations
      }
    });

    return {
      valid: isValid,
      error: isValid ? undefined : 'Policy validation failed',
      violations: violations.length > 0 ? violations : undefined
    };
  }

  private validateSchema(input: any, schema: any): ValidationResult {
    const validate = this.ajv.compile(schema);
    const valid = validate(input);

    return {
      valid: !!valid,
      violations: valid ? undefined : (validate.errors || undefined)
    };
  }

  private validateNaming(input: any): ValidationResult {
    const violations: string[] = [];

    if (input.filePath) {
      const filename = input.filePath.split('/').pop();

      // Check for kebab-case in filenames
      if (filename && !/^[a-z0-9-]+(\.[a-z]+)?$/.test(filename)) {
        violations.push(`Filename ${filename} does not follow kebab-case convention`);
      }
    }

    return {
      valid: violations.length === 0,
      violations: violations.length > 0 ? violations : undefined
    };
  }

  private validatePath(filePath: string): ValidationResult {
    const violations: string[] = [];

    // Check path structure
    if (filePath.includes('..')) {
      violations.push('Path contains parent directory reference (..)');
    }

    // Check for absolute paths
    if (filePath.startsWith('/')) {
      violations.push('Absolute paths are not allowed');
    }

    // Check path length
    if (filePath.length > 255) {
      violations.push('Path length exceeds 255 characters');
    }

    return {
      valid: violations.length === 0,
      violations: violations.length > 0 ? violations : undefined
    };
  }

  private validateSemantic(input: any): ValidationResult {
    const violations: string[] = [];

    // Check for required semantic anchors
    if (!input.semanticAnchor && !input.metadata?.semanticAnchor) {
      violations.push('Missing semantic anchor');
    }

    // Check for GL layer specification
    if (!input.layer && !input.metadata?.layer) {
      violations.push('Missing GL layer specification');
    }

    // Check for charter version
    if (!input.charterVersion && !input.metadata?.charterVersion) {
      violations.push('Missing charter version');
    }

    return {
      valid: violations.length === 0,
      violations: violations.length > 0 ? violations : undefined
    };
  }

  private validateGovernanceTags(input: any): ValidationResult {
    const violations: string[] = [];

    // Check for @GL-governed tag
    const content = input.content || '';
    if (!content.includes('@GL-governed')) {
      violations.push('Missing @GL-governed tag');
    }

    // Check for @GL-layer tag
    if (!content.includes('@GL-layer')) {
      violations.push('Missing @GL-layer tag');
    }

    // Check for @GL-semantic tag
    if (!content.includes('@GL-semantic')) {
      violations.push('Missing @GL-semantic tag');
    }

    return {
      valid: violations.length === 0,
      violations: violations.length > 0 ? violations : undefined
    };
  }

  public setPolicies(policies: PolicyConfig): void {
    this.policies = { ...this.policies, ...policies };
  }
}

// @GL-governed
// @GL-layer: GL90-99
// @GL-semantic: artifact-store
// @GL-charter-version: 2.0.0

import fs from 'fs/promises';
import path from 'path';
import { createLogger } from '../utils/logger';

const logger = createLogger('ArtifactStore');

export interface Artifact {
  id: string;
  type: 'audit-report' | 'patch' | 'metadata' | 'validation-result';
  name: string;
  content: any;
  timestamp: string;
  metadata: any;
}

export class ArtifactStore {
  private artifactsPath: string;
  private artifacts: Map<string, Artifact> = new Map();

  constructor() {
    this.artifactsPath = path.join(process.cwd(), 'storage', 'gl-artifacts');
  }

  public async storeArtifact(artifact: Artifact): Promise<void> {
    this.artifacts.set(artifact.id, artifact);
    await this.persistArtifact(artifact);
    logger.info(`Artifact stored: ${artifact.id}`);
  }

  private async persistArtifact(artifact: Artifact): Promise<void> {
    try {
      const artifactPath = path.join(this.artifactsPath, artifact.type);
      await fs.mkdir(artifactPath, { recursive: true });

      const filePath = path.join(artifactPath, `${artifact.id}.json`);
      await fs.writeFile(filePath, JSON.stringify(artifact, null, 2));
    } catch (error: any) {
      logger.error(`Failed to persist artifact: ${error.message}`);
      throw error;
    }
  }

  public async loadArtifact(id: string): Promise<Artifact | null> {
    const cached = this.artifacts.get(id);
    if (cached) return cached;

    try {
      const artifactPath = path.join(this.artifactsPath);
      const files = await fs.readdir(artifactPath, { recursive: true });

      for (const dir of files) {
        try {
          const filePath = path.join(this.artifactsPath, dir, `${id}.json`);
          const content = await fs.readFile(filePath, 'utf-8');
          const artifact = JSON.parse(content);
          this.artifacts.set(id, artifact);
          return artifact;
        } catch {
          continue;
        }
      }

      return null;
    } catch (error: any) {
      logger.error(`Failed to load artifact: ${error.message}`);
      return null;
    }
  }

  public async getArtifactsByType(type: string): Promise<Artifact[]> {
    const allArtifacts: Artifact[] = [];

    try {
      const typePath = path.join(this.artifactsPath, type);
      const files = await fs.readdir(typePath);

      for (const file of files) {
        const artifactId = file.replace('.json', '');
        const artifact = await this.loadArtifact(artifactId);
        if (artifact) {
          allArtifacts.push(artifact);
        }
      }
    } catch (error: any) {
      if (error.code !== 'ENOENT') {
        logger.error(`Failed to get artifacts by type: ${error.message}`);
      }
    }

    return allArtifacts;
  }

  public async deleteArtifact(id: string): Promise<void> {
    this.artifacts.delete(id);

    try {
      const artifactPath = path.join(this.artifactsPath);
      const files = await fs.readdir(artifactPath, { recursive: true });

      for (const dir of files) {
        try {
          const filePath = path.join(this.artifactsPath, dir, `${id}.json`);
          await fs.unlink(filePath);
          logger.info(`Artifact deleted: ${id}`);
          return;
        } catch {
          continue;
        }
      }
    } catch (error: any) {
      logger.error(`Failed to delete artifact: ${error.message}`);
    }
  }

  public getArtifactCount(): number {
    return this.artifacts.size;
  }

  public clearArtifacts(): void {
    this.artifacts.clear();
  }
}

// @GL-governed
// @GL-layer: GL90-99
// @GL-semantic: logger-utility
// @GL-charter-version: 2.0.0

import winston from 'winston';
import path from 'path';

export function createLogger(context: string): winston.Logger {
  const logFormat = winston.format.combine(
    winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
    winston.format.errors({ stack: true }),
    winston.format.splat(),
    winston.format.json()
  );

  const consoleFormat = winston.format.combine(
    winston.format.colorize(),
    winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
    winston.format.printf(({ timestamp, level, message, context: ctx, ...metadata }) => {
      let msg = `${timestamp} [${level}] [${ctx}] ${message}`;
      if (Object.keys(metadata).length > 0) {
        msg += ` ${JSON.stringify(metadata)}`;
      }
      return msg;
    })
  );

  const transports: winston.transport[] = [
    new winston.transports.Console({
      format: consoleFormat
    })
  ];

  // Add file transport if logs directory exists
  try {
    const logsPath = path.join(process.cwd(), 'storage', 'logs');
    require('fs').mkdirSync(logsPath, { recursive: true });

    transports.push(
      new winston.transports.File({
        filename: path.join(logsPath, 'combined.log'),
        format: logFormat
      }),
      new winston.transports.File({
        filename: path.join(logsPath, 'error.log'),
        level: 'error',
        format: logFormat
      })
    );
  } catch (error) {
    // Ignore error if we can't create log directory
  }

  return winston.createLogger({
    level: process.env.LOG_LEVEL || 'info',
    format: logFormat,
    defaultMeta: { context },
    transports
  });
}

